mapper.select:
/* com.sgg.learn.mapper.LearnMapper.getList */ SELECT CODE_NO , CODE_TYPE_CD , CODE_TITLE FROM ( SELECT CODE_NO , (SELECT CD_DTL_NAME FROM SGG_CD_DTL WHERE 1 = 1 AND CD_DTL = CODE_TYPE_CD AND CD_ID = 'CODE_TYPE_CD' AND DEL_YN = 'N') AS CODE_TYPE_CD , CODE_TITLE , ROW_NUMBER() OVER(ORDER BY CODE_NO) AS RNUM FROM SGG_CMN_CODE WHERE 1 = 1 AND DEL_YN = 'N'
AND CODE_TYPE_CD = #{type}
AND CODE_TITLE LIKE '%' || #{title} || '%'
) WHERE RNUM BETWEEN ((#{page} - 1) * #{row}) + 1 AND #{page} * #{row}

/* com.sgg.learn.mapper.LearnMapper.getListCnt */ SELECT COUNT(*) FROM ( SELECT ROW_NUMBER() OVER(ORDER BY CODE_NO) AS RNUM , CODE_NO , (SELECT CD_DTL_NAME FROM SGG_CD_DTL WHERE 1 = 1 AND CD_DTL = CODE_TYPE_CD AND CD_ID = 'CODE_TYPE_CD' AND DEL_YN = 'N') AS CODE_TYPE_CD , CODE_TITLE FROM SGG_CMN_CODE A1 WHERE 1 = 1 AND DEL_YN = 'N'
AND CODE_TYPE_CD = #{type}
AND CODE_TITLE LIKE '%' || #{title} || '%'
)

/* com.sgg.learn.mapper.LearnMapper.getCodeNo */
SELECT NVL(MAX(CODE_NO), 0) + 1 AS CODE_NO
FROM SGG_CMN_CODE

mapper.insert:
/* com.sgg.learn.mapper.LearnMapper.doSave */
MERGE INTO SGG_CMN_CODE A1
USING DUAL
ON (A1.CODE_NO = #{codeNo})
WHEN MATCHED THEN
UPDATE
SET A1.CODE_TYPE_CD = #{codeTypeCd}
    , A1.CODE_TITLE = #{codeTitle}
    , A1.DEL_YN = #{delYn}
WHEN NOT MATCHED THEN
INSERT
(	A1.CODE_NO
    , A1.CODE_TYPE_CD
    , A1.CODE_TITLE
    , A1.USER_ID
    , A1.DEL_YN
)
VALUES
(	#{codeNo}
    , #{codeTypeCd}
    , #{codeTitle}
    , #{userId}
    , 'N'
)

/* com.sgg.learn.mapper.LearnMapper.doSaveDtl */

    SELECT NVL(MAX(COL_NO), 0) + 1 AS COL_NO
    FROM SGG_CMN_CODE_DTL

INSERT INTO SGG_CMN_CODE_DTL
(   CODE_NO
    , DIV_NO
    , DIV_NO_NO
    , DIV_CODE
    , COL_NO
)
VALUES
(   #{codeNo}
    , #{divNo}
    , #{divNoNo}
    , #{divCode}
    , #{colNo}
)

mapper.delete:
/* com.sgg.learn.mapper.LearnMapper.delDtl */
DELETE FROM SGG_CMN_CODE_DTL
WHERE CODE_NO = #{codeNo}
